<script>
import "@vueform/multiselect/themes/default.css";
import Product from "./Quiz/Product";

import concernCopy from "../mixins/concernCopy";
import animation from "../mixins/animation";
import "vue3-carousel/dist/carousel.css";
import { Carousel, Slide, Pagination, Navigation } from "vue3-carousel";
import Concern from "./Quiz/Concern";
import Timer from "./Quiz/Timer";
import { GoogleMap, Marker } from "vue3-google-map";
import CarouselBreakpoints from "../mixins/CarouselBreakpoints";

export default {
  name: "Results",
  mixins: [concernCopy, CarouselBreakpoints, animation],
  props: ["email"],
  components: {
    Product,
    Carousel,
    Slide,
    Pagination,
    Navigation,
    Concern,
    GoogleMap,
    Marker,
    Timer,
  },
  data() {
    return {
      isReady: false,
      isAdding: false,
      results: [],
    };
  },
  computed: {
    expirationDate() {
      const minutes = 10;
      var date = new Date();
      return new Date(date.getTime() + minutes * 60000);
    },
    listLifeStyle() {
      if (!this.results || !this.results.lifestyle) {
        return [];
      }
      return this.results.lifestyle.filter(
        (e) => e.ingredients.length > 0 && e.description !== "Not Set"
      );
    },

    listInteLifeStyle() {
      if (!this.results || !this.results.lifestyle) {
        return [];
      }
      const inte = this.results.lifestyle.filter(
        (e) => e.ingredients.length > 0 && e.description !== "Not Set"
      );

      let arr = [];
      inte.forEach((e) => {
        e.ingredients.forEach((idata) => {
          idata.lifeStyleTitle = e.description;
          arr.push(idata);
        });
      });
      return arr;
    },
  },
  async mounted() {
    let email = this.email;
    const urlParams = new URLSearchParams(window.location.search);
    const emailParam = urlParams.get("email");
    if (this.$route.query.email) {
      email = this.$route.query.email;
    }

    if (emailParam) {
      email = emailParam;
    }
    console.log(email)

    this.localQuiz = localStorage.getItem("quiz");

    if (!this.localQuiz || email) {
      if (email) {
        console.log('em: ' + email)
        fetch(`${this.base_url}/api/customer/${email}`, {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: "Bearer " + this.authToken,
          },
        })
          .then((response) => {
            console.log(response)
            if (response.status === 404) {
              window.location.href = "/pages/quiz";
              return;
            }
            response.json().then(async (rs) => {
              if (rs.data.id) {
                this.localQuiz = rs.data;
                this.initData();
              }
            });
          })
          .catch((e) => {});

        //check for email here pull lead id if email is passed and query api for lead ID from customer
        //if there is no lead id for this redirect to quiz
        //if there is no email redirect to login and add checkout_url=/pages/your-quiz-results
      } else {
        // this.$router.push("/");
        window.location.href =
          "/account/login?checkout_url=/pages/your-quiz-results";
        return;
      }
      return;
    }
    this.localQuiz = JSON.parse(this.localQuiz);
    this.initData();
  },
  methods: {
    async addToCart() {
      this.isAdding = true;
      await fetch("/cart/clear.js", {
        method: "POST",
      });
      const result = await fetch("/cart/add.json", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
        body: JSON.stringify({
          id: 39956980826155,
          quantity: 1,
          selling_plan: 506331179,
        }),
      });
      window.location.href = "/checkout?discount=TRYBEFOREBUY";
      this.isAdding = false;
    },
    async initData() {
      const response = await fetch(
        `${this.base_url}/api/quiz/1/lead/${this.localQuiz.id}/results`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: "Bearer " + this.authToken,
          },
        }
      );
      response.json().then(async (rs) => {
        this.results = rs.data;
      });
    },
    onImgLoad() {
      const timeout = this.debug ? 1 : 21130;
      setTimeout(() => {
        this.isReady = true;
        this.$nextTick(() => {
          animation.check_if_in_view();
          animation.slideNavBar();
        });
      }, timeout);
    },
    scrollSmoothTo(type) {
      animation.scrollSmoothTo(type);
    },
    scrollSmoothToPosition(type) {
      animation.scrollSmoothToPosition(type);
    },
  },
};
</script>

